val map = mapOf("admin" to "admin", "user1" to "user1", "user2" to "user2")const val help = ("blach-blach")val user = User()val reg = Regex("^[a-zA-Z0-9]+$")fun main(args: Array<String>) {    if (args.isEmpty()) {        println(help)    } else {        if (args.size == 4) {            pairOfLogPass(args)            if (!correctLogin(user.login)) {                println(2)            } else {                if (map[user.login] == null) println(3)                else if (map[user.login] != user.pass) println(4)                else if (map[user.login] != null && map[user.login] == user.pass) println("succeed")                else println(help)            }        }        else println(help)    }}fun pairOfLogPass(arr: Array<String>) {    if (arr[0] == "-login") {        user.login = arr[1]        if (arr[2] == "-pass") {            user.pass = arr[3]        } else println(help)    } else if (arr[0] == "-pass") {        user.pass = arr[1]        if (arr[2] == "-login") {            user.login = arr[3]        } else println(help)    }}fun correctLogin(s: String): Boolean = reg.matches(s)class User(var login: String = "", var pass: String = "")